import { useEffect, useState } from "react";
import {
  getCategories,
  createCategory,
  updateCategory,
  deleteCategory,
} from "../../api/category.api";

export default function Categories() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  const [form, setForm] = useState({
    name: "",
    description: "",
    imageUrl: "",
    isActive: true,
  });

  const [editingId, setEditingId] = useState(null);
  const [msg, setMsg] = useState("");

  const load = async () => {
    setLoading(true);
    setMsg("");
    try {
      const res = await getCategories();
      setItems(res.data);
    } catch (e) {
      setMsg(e?.response?.data?.message || "Failed to load categories");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    load();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    setMsg("");

    try {
      if (editingId) {
        await updateCategory(editingId, form);
        setMsg("Category updated ✅");
      } else {
        await createCategory(form);
        setMsg("Category created ✅");
      }

      setForm({ name: "", description: "", imageUrl: "", isActive: true });
      setEditingId(null);
      await load();
    } catch (e) {
      setMsg(e?.response?.data?.message || "Operation failed");
    }
  };

  const onEdit = (cat) => {
    setEditingId(cat.id);
    setForm({
      name: cat.name || "",
      description: cat.description || "",
      imageUrl: cat.imageUrl || "",
      isActive: cat.isActive ?? true,
    });
    setMsg("");
  };

  const onCancelEdit = () => {
    setEditingId(null);
    setForm({ name: "", description: "", imageUrl: "", isActive: true });
  };

  const onDelete = async (id) => {
    if (!confirm("Delete this category?")) return;
    setMsg("");
    try {
      await deleteCategory(id);
      setMsg("Category deleted ✅");
      await load();
    } catch (e) {
      setMsg(e?.response?.data?.message || "Delete failed");
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 900, margin: "0 auto" }}>
      <h2>Categories (Admin)</h2>

      <form onSubmit={onSubmit} style={{ border: "1px solid #ddd", padding: 16, borderRadius: 10 }}>
        <h3 style={{ marginTop: 0 }}>{editingId ? "Edit Category" : "Create Category"}</h3>

        <div style={{ display: "grid", gap: 10 }}>
          <input
            placeholder="Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />

          <textarea
            placeholder="Description"
            value={form.description}
            onChange={(e) => setForm({ ...form, description: e.target.value })}
            rows={3}
          />

          <input
            placeholder="Image URL (optional)"
            value={form.imageUrl}
            onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
          />

          <label style={{ display: "flex", gap: 8, alignItems: "center" }}>
            <input
              type="checkbox"
              checked={form.isActive}
              onChange={(e) => setForm({ ...form, isActive: e.target.checked })}
            />
            Active
          </label>

          <div style={{ display: "flex", gap: 10 }}>
            <button type="submit">{editingId ? "Update" : "Create"}</button>
            {editingId && (
              <button type="button" onClick={onCancelEdit}>
                Cancel
              </button>
            )}
          </div>
        </div>

        {msg && <p style={{ marginBottom: 0 }}>{msg}</p>}
      </form>

      <div style={{ marginTop: 20 }}>
        <h3>Category List</h3>

        {loading ? (
          <p>Loading...</p>
        ) : items.length === 0 ? (
          <p>No categories yet.</p>
        ) : (
          <table width="100%" cellPadding="10" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr style={{ background: "#f5f5f5" }}>
                <th align="left">Name</th>
                <th align="left">Description</th>
                <th align="left">Active</th>
                <th align="left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map((c) => (
                <tr key={c.id} style={{ borderBottom: "1px solid #eee" }}>
                  <td>{c.name}</td>
                  <td>{c.description}</td>
                  <td>{c.isActive ? "Yes" : "No"}</td>
                  <td style={{ display: "flex", gap: 8 }}>
                    <button onClick={() => onEdit(c)}>Edit</button>
                    <button onClick={() => onDelete(c.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}